import React from "react";
import ReactDOM from "react-dom";

//const element = <h1>Hello!</h1>;

class Kompo extends React.Component {
  constructor(props) {
    super(props);
    this.state = { activeClasses: [false, false, false] };
    this.addActiveClass = this.addActiveClass.bind(this);
  }

  addActiveClass(index) {
    const activeClasses = [
      ...this.state.activeClasses.slice(0, index),
      !this.state.activeClasses[index],
      this.state.activeClasses.slice(index + 1)
    ];
    this.setState({ activeClasses });
  }

  render() {
    const activeClasses = this.activeClasses.slice();
    return (
      <div>
        <div
          className={activeClasses[0] ? "active" : "inactive"}
          onClick={() => this.addActiveClass(0)}
        >
          <p>0</p>
        </div>
        <div
          className={activeClasses[1] ? "active" : "inactive"}
          onClick={() => this.addActiveClass(1)}
        >
          <p>1</p>
        </div>
        <div onClick={() => this.addActiveClass(2)}>
          <p>2</p>
        </div>
      </div>
    );
  }
}

ReactDOM.render(<Kompo />, document.getElementById("root"));
